import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  padding: 20px;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 40px;
`;

const BackButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  font-size: 24px;
  margin-right: 20px;
  padding: 0;
`;

const Title = styled.h1`
  font-size: 32px;
  margin: 0;
  font-weight: 600;
`;

const Subtitle = styled.h2`
  font-size: 32px;
  margin: 0 0 40px 0;
  font-weight: 600;
`;

const AgentsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
  gap: 20px;
  margin-bottom: 40px;
`;

const AgentCard = styled.div`
  aspect-ratio: 1;
  background-color: ${props => props.selected ? '#e1f5fe' : '#f0f0f0'};
  border-radius: 12px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  overflow: hidden;
  position: relative;
  border: ${props => props.selected ? '2px solid #007aff' : '2px solid transparent'};
  
  &:hover {
    background-color: ${props => props.selected ? '#e1f5fe' : '#e0e0e0'};
  }
`;

const AgentImage = styled.div`
  width: 60%;
  height: 60%;
  margin-bottom: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  
  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

const AgentPlaceholder = styled.div`
  width: 60%;
  height: 60%;
  border-radius: 50%;
  background-color: #ddd;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  color: #666;
`;

const AgentName = styled.div`
  font-size: 16px;
  font-weight: 500;
  text-align: center;
`;

const AddAgentCard = styled.div`
  aspect-ratio: 1;
  background-color: white;
  border: 2px dashed #ccc;
  border-radius: 12px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  
  &:hover {
    border-color: #999;
  }
`;

const AddIcon = styled.div`
  font-size: 32px;
  color: #999;
  margin-bottom: 8px;
`;

const StartButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: auto;
  padding: 20px 0;
`;

const StartButton = styled.button`
  padding: 16px 48px;
  background-color: #007aff;
  color: white;
  border: none;
  border-radius: 24px;
  font-size: 18px;
  font-weight: 500;
  cursor: pointer;
  
  &:hover {
    background-color: #0062cc;
  }
  
  &:disabled {
    background-color: #cccccc;
    cursor: default;
  }
`;

const SelectAIPartners = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [aiAgents, setAiAgents] = useState([]);
  const [selectedAgents, setSelectedAgents] = useState([]);
  const [loading, setLoading] = useState(true);
  
  // Get the room ID from location state
  const roomId = location.state?.roomId;
  
  useEffect(() => {
    // Redirect if no room ID
    if (!roomId) {
      navigate('/');
      return;
    }
    
    // Fetch available AI agents
    const fetchAgents = async () => {
      try {
        const response = await axios.get('/api/ai-agents/');
        setAiAgents(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching AI agents:", error);
        setLoading(false);
        // If we can't fetch real agents, use some demo data
        setAiAgents([
          { id: 1, name: 'Designer', role: 'Designer' },
          { id: 2, name: 'Engineer', role: 'Engineer' },
          { id: 3, name: 'Finance', role: 'Finance' },
          { id: 4, name: 'Professor', role: 'Professor' }
        ]);
      }
    };
    
    fetchAgents();
  }, [roomId, navigate]);
  
  const toggleAgentSelection = (agent) => {
    if (selectedAgents.some(a => a.id === agent.id)) {
      // Remove agent if already selected
      setSelectedAgents(selectedAgents.filter(a => a.id !== agent.id));
    } else {
      // Add agent if not yet selected
      setSelectedAgents([...selectedAgents, agent]);
    }
  };
  
  const handleBackClick = () => {
    navigate('/');
  };
  
  const handleStart = () => {
    // Navigate to the video room with selected AI agents
    navigate(`/room/${roomId}`, { 
      state: { 
        aiPartners: selectedAgents 
      } 
    });
  };
  
  if (loading) {
    return (
      <Container>
        <Header>
          <BackButton onClick={handleBackClick}>←</BackButton>
          <Title>Loading...</Title>
        </Header>
      </Container>
    );
  }
  
  return (
    <Container>
      <Header>
        <BackButton onClick={handleBackClick}>←</BackButton>
      </Header>
      
      <Subtitle>Select<br/>AI partners...</Subtitle>
      
      <AgentsGrid>
        {aiAgents.map(agent => (
          <AgentCard 
            key={agent.id}
            selected={selectedAgents.some(a => a.id === agent.id)}
            onClick={() => toggleAgentSelection(agent)}
          >
            <AgentImage>
              {agent.avatar ? (
                <img src={agent.avatar} alt={agent.name} />
              ) : (
                <AgentPlaceholder>{agent.name[0]}</AgentPlaceholder>
              )}
            </AgentImage>
            <AgentName>{agent.role}</AgentName>
          </AgentCard>
        ))}
        
        <AddAgentCard onClick={() => alert("Custom AI agent creation is not available in this demo.")}>
          <AddIcon>+</AddIcon>
        </AddAgentCard>
      </AgentsGrid>
      
      <StartButtonContainer>
        <StartButton 
          onClick={handleStart}
          disabled={selectedAgents.length === 0}
        >
          Start
        </StartButton>
      </StartButtonContainer>
    </Container>
  );
};

export default SelectAIPartners;
