// frontend/src/components/SelectAIPartners.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  padding: 12px;
  height: 100vh;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 24px;
`;

const BackButton = styled.button`
  background: none;
  border: none;
  font-size: 20px;
  cursor: pointer;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-left: 12px;
`;

const AIGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr); /* 2 columns for smaller screen */
  gap: 16px;
  margin-bottom: 24px;
`;

const AIOption = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  padding: 5px;
  border-radius: 10px;
`;

const AIAvatar = styled.div`
  width: 80px;
  height: 80px;
  background-color: #e0e0e0;
  border-radius: 10px;
  margin-bottom: 8px;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  border: ${props => props.selected ? '3px solid rgb(197, 152, 54)' : 'none'};
`;

const AIImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const AIRole = styled.div`
  font-size: 14px;
  font-weight: bold;
  text-align: center;
`;

const AddButton = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 80px;
  height: 80px;
  background-color: #f0f0f0;
  border: 2px dashed #aaa;
  border-radius: 10px;
  cursor: pointer;
  font-size: 24px;
`;

const StartButton = styled.button`
  padding: 12px 24px;
  font-size: 16px;
  background-color: #e0e0e0;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  align-self: center;
  margin-top: auto;

  &:hover {
    background-color: #d0d0d0;
  }
`;

const LoadingSpinner = styled.div`
  margin-left: 10px;
  display: inline-block;
  width: 16px;
  height: 16px;
  border: 2px solid rgba(0, 0, 0, 0.1);
  border-top: 2px solid #333;
  border-radius: 50%;
  animation: spin 1s linear infinite;
  
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const SelectAIPartners = () => {
  const [aiAgents, setAiAgents] = useState([
    { id: 1, role: 'Designer', avatar: '/img/designer.png' },
    { id: 2, role: 'Engineer', avatar: '/img/engineer.png' },
    { id: 3, role: 'Finance', avatar: '/img/finance.png' },
    { id: 4, role: 'Professor', avatar: '/img/professor.png' }
  ]);
  const [selectedAgents, setSelectedAgents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [loadingSavedAgents, setLoadingSavedAgents] = useState(true);
  
  const navigate = useNavigate();
  const location = useLocation();
  const roomId = location.state?.roomId;
  
  useEffect(() => {
    // Fetch AI agents from backend
    const fetchAIAgents = async () => {
      try {
        const response = await axios.get('/api/ai-agents/');
        if (response.data.length > 0) {
          setAiAgents(response.data);
        }
      } catch (error) {
        console.error('Error fetching AI agents:', error);
      }
    };
    
    fetchAIAgents();
    
    // Check if there are already selected AI partners for this room
    const fetchRoomAIPartners = async () => {
      if (roomId) {
        try {
          setLoadingSavedAgents(true);
          const response = await axios.get(`/api/rooms/${roomId}/ai-partners`);
          
          if (response.data && response.data.aiPartners && response.data.aiPartners.length > 0) {
            // Map the returned AI partners to our local format
            const savedAgents = response.data.aiPartners.map(partner => ({
              id: partner.id,
              role: partner.role,
              avatar: partner.avatar || `/placeholder-${partner.role.toLowerCase()}.png`
            }));
            
            setSelectedAgents(savedAgents);
          }
        } catch (error) {
          console.error('Error fetching room AI partners:', error);
        } finally {
          setLoadingSavedAgents(false);
        }
      } else {
        setLoadingSavedAgents(false);
      }
    };
    
    fetchRoomAIPartners();
  }, [roomId]);
  
  const toggleAgent = (agent) => {
    if (selectedAgents.find(a => a.id === agent.id)) {
      setSelectedAgents(selectedAgents.filter(a => a.id !== agent.id));
    } else {
      setSelectedAgents([...selectedAgents, agent]);
    }
  };
  
  const handleStart = async () => {
    // Check if roomId exists
    if (!roomId) {
      console.error('Room ID is missing');
      alert('無法加入房間，缺少房間 ID');
      return;
    }
    
    // Check identity - try both possible keys to get username
    const username = localStorage.getItem('username') || localStorage.getItem('userName');
    
    if (!username) {
      console.error('Username not found in localStorage');
      alert('請先設定您的用戶名稱');
      navigate('/');
      return;
    }
    
    setIsLoading(true);
    
    try {
      console.log(`Joining room ${roomId} as ${username} with selected AI partners`);
      
      // Format selected AI partners
      const aiPartners = selectedAgents.map(agent => ({
        id: agent.id,
        name: agent.role,
        role: agent.role,
        avatar: agent.avatar
      }));
      
      // Save the selected AI partners to the database for this room
      await axios.post(`/api/rooms/${roomId}/ai-partners`, {
        aiPartners: aiPartners
      });
      
      // Navigate to the room with AI partners as state
      navigate(`/room/${roomId}`, {
        state: {
          aiPartners: aiPartners
        }
      });
    } catch (error) {
      console.error('Error saving AI partners or starting room:', error);
      alert('儲存 AI 伙伴或加入房間時發生錯誤，請稍後再試');
      setIsLoading(false);
    }
  };
  
  if (loadingSavedAgents) {
    return (
      <Container>
        <Header>
          <BackButton onClick={() => navigate('/')}>←</BackButton>
          <Title>Loading AI partners...</Title>
        </Header>
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '50px' }}>
          <LoadingSpinner />
        </div>
      </Container>
    );
  }
  
  return (
    <Container>
      <Header>
        <BackButton onClick={() => navigate('/')}>←</BackButton>
        <Title>Select AI partners...</Title>
      </Header>
      
      <AIGrid>
        {aiAgents.map((agent) => (
          <AIOption 
            key={agent.id} 
            selected={selectedAgents.some(a => a.id === agent.id)}
            onClick={() => toggleAgent(agent)}
          >
            <AIAvatar selected={selectedAgents.some(a => a.id === agent.id)}>
              <AIImage src={agent.avatar || `/placeholder-${agent.role.toLowerCase()}.png`} alt={agent.role} />
            </AIAvatar>
            <AIRole>{agent.role}</AIRole>
          </AIOption>
        ))}
        <AIOption>
          <AddButton>+</AddButton>
        </AIOption>
      </AIGrid>
      
      <StartButton 
        onClick={handleStart} 
        disabled={isLoading}
      >
        {isLoading ? (
          <>
            Loading... <LoadingSpinner />
          </>
        ) : 'Start'}
      </StartButton>
    </Container>
  );
};

export default SelectAIPartners;
