import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";
import Peer from "simple-peer";
import styled from "styled-components";

const socket = io.connect("http://localhost:3001");

function VideoCallRoom({ roomId, userName }) {
  const [peers, setPeers] = useState([]);
  const [videoEnabled, setVideoEnabled] = useState(true);
  const userVideo = useRef();

  useEffect(() => {
    navigator.mediaDevices.getUserMedia({ video: true, audio: true }).then((stream) => {
      if (userVideo.current) {
        userVideo.current.srcObject = stream;
      }

      socket.emit("join room", roomId, userName);

      socket.on("all users", (users) => {
        const peers = users.map((user) => {
          const peer = createPeer(user.id, socket.id, stream);
          return { peerID: user.id, peer, name: user.name };
        });
        setPeers(peers);
      });

      socket.on("user joined", (payload) => {
        const peer = addPeer(payload.signal, payload.callerID, stream);
        setPeers((prevPeers) => [...prevPeers, { peerID: payload.callerID, peer, name: payload.name }]);
      });

      socket.on("receiving returned signal", (payload) => {
        const item = peers.find((p) => p.peerID === payload.id);
        if (item) {
          item.peer.signal(payload.signal);
        }
      });
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  function createPeer(userToSignal, callerID, stream) {
    const peer = new Peer({ initiator: true, trickle: false, stream });
    peer.on("signal", (signal) => {
      socket.emit("sending signal", { userToSignal, callerID, signal, name: userName });
    });
    return peer;
  }

  function addPeer(incomingSignal, callerID, stream) {
    const peer = new Peer({ initiator: false, trickle: false, stream });
    peer.signal(incomingSignal);
    return peer;
  }

  return (
    <Container>
      <Header>
        <RoomTitle>Room {roomId}</RoomTitle>
      </Header>

      <VideoGrid>
        {peers.map((peerObj, index) => (
          <ParticipantVideoWrapper key={peerObj.peerID}>
            <ParticipantVideoElement peer={peerObj.peer} />
            <ParticipantName>{peerObj.name}</ParticipantName>
          </ParticipantVideoWrapper>
        ))}
      </VideoGrid>

      {/* 自己的鏡頭縮小並顯示於右下角 */}
      {videoEnabled && (
        <MyVideoWrapper>
          <ParticipantVideo ref={userVideo} autoPlay muted playsInline />
          <ParticipantName>{userName} (You)</ParticipantName>
        </MyVideoWrapper>
      )}

      <ToggleButton onClick={() => setVideoEnabled((prev) => !prev)}>
        {videoEnabled ? "關閉鏡頭" : "開啟鏡頭"}
      </ToggleButton>
    </Container>
  );
}

const Container = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
`;

const Header = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 12px 16px;
  border-bottom: 1px solid #e0e0e0;
`;

const RoomTitle = styled.h2`
  font-size: 20px;
  font-weight: bold;
`;

const VideoGrid = styled.div`
  flex: 1;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  padding: 16px;
`;

const ParticipantVideoWrapper = styled.div`
  position: relative;
  margin: 10px;
  width: 300px;
  height: 225px;
  background: #000;
  border-radius: 8px;
  overflow: hidden;
`;

const MyVideoWrapper = styled.div`
  position: fixed;
  bottom: 80px;
  right: 20px;
  width: 200px;
  aspect-ratio: 4 / 3;
  background-color: #000;
  border-radius: 8px;
  overflow: hidden;
  z-index: 10;
`;

const ParticipantVideo = styled.video`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const ParticipantName = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  background: rgba(0, 0, 0, 0.6);
  color: white;
  padding: 4px 8px;
  font-size: 14px;
`;

const ToggleButton = styled.button`
  position: fixed;
  bottom: 20px;
  right: 20px;
  padding: 8px 12px;
  background-color: #2e7d32;
  color: white;
  border: none;
  border-radius: 6px;
  cursor: pointer;
`;

function ParticipantVideoElement({ peer }) {
  const ref = useRef();

  useEffect(() => {
    peer.on("stream", (stream) => {
      if (ref.current) {
        ref.current.srcObject = stream;
      }
    });
  }, []);

  return <ParticipantVideo ref={ref} autoPlay playsInline />;
}

export default VideoCallRoom;
