// frontend/src/components/SelectAIPartners.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  padding: 20px;
  height: 100vh;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 40px;
`;

const BackButton = styled.button`
  background: none;
  border: none;
  font-size: 24px;
  cursor: pointer;
`;

const Title = styled.h1`
  font-size: 32px;
  margin-left: 20px;
`;

const AIGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
  margin-bottom: 40px;
`;

const AIOption = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  padding: 15px;
  background-color: ${props => props.selected ? '#e0e0e0' : '#f0f0f0'};
  border-radius: 10px;
`;

const AIAvatar = styled.div`
  width: 100px;
  height: 100px;
  background-color: #e0e0e0;
  border-radius: 10px;
  margin-bottom: 10px;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const AIImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const AIRole = styled.div`
  font-size: 18px;
  font-weight: bold;
`;

const AddButton = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100px;
  height: 100px;
  background-color: #f0f0f0;
  border: 2px dashed #aaa;
  border-radius: 10px;
  cursor: pointer;
  font-size: 32px;
`;

const StartButton = styled.button`
  padding: 15px 30px;
  font-size: 18px;
  background-color: #e0e0e0;
  border: none;
  border-radius: 30px;
  cursor: pointer;
  align-self: center;
  margin-top: auto;
  
  &:hover {
    background-color: #d0d0d0;
  }
`;

const SelectAIPartners = () => {
  const [aiAgents, setAiAgents] = useState([
    { id: 1, role: 'Designer', avatar: 'C:/Users/user/OneDrive/桌面/HCI+D/video_app/frontend/src/img/designer.png' },
    { id: 2, role: 'Engineer', avatar: '../img/engineer.png' },
    { id: 3, role: 'Finance', avatar: '../img/finance.png' },
    { id: 4, role: 'Professor', avatar: '../img/professor.png' }
  ]);
  const [selectedAgents, setSelectedAgents] = useState([]);
  
  const navigate = useNavigate();
  const location = useLocation();
  const params = new URLSearchParams(location.search);
  const roomId = params.get('room');
  
  useEffect(() => {
    // Fetch AI agents from backend
    const fetchAIAgents = async () => {
      try {
        const response = await axios.get('/api/ai-agents/');
        if (response.data.length > 0) {
          setAiAgents(response.data);
        }
      } catch (error) {
        console.error('Error fetching AI agents:', error);
      }
    };
    
    fetchAIAgents();
  }, []);
  
  const toggleAgent = (agent) => {
    if (selectedAgents.find(a => a.id === agent.id)) {
      setSelectedAgents(selectedAgents.filter(a => a.id !== agent.id));
    } else {
      setSelectedAgents([...selectedAgents, agent]);
    }
  };
  
  const handleStart = async () => {
    const username = localStorage.getItem('username');
    
    if (!username) {
      navigate('/');
      return;
    }
    
    try {
      // Join room as a user
      await axios.post(`/api/rooms/${roomId}/join/`, {
        name: username,
        is_ai: false
      });
      
      // Add selected AI agents to room
      for (const agent of selectedAgents) {
        await axios.post(`/api/rooms/${roomId}/join/`, {
          name: agent.role,
          is_ai: true,
          ai_agent: agent.id
        });
      }
      
      navigate(`/room/${roomId}`);
    } catch (error) {
      console.error('Error joining room:', error);
    }
  };
  
  return (
    <Container>
      <Header>
        <BackButton onClick={() => navigate('/')}>←</BackButton>
        <Title>Select AI partners...</Title>
      </Header>
      
      <AIGrid>
        {aiAgents.map((agent) => (
          <AIOption 
            key={agent.id} 
            selected={selectedAgents.some(a => a.id === agent.id)}
            onClick={() => toggleAgent(agent)}
          >
            <AIAvatar>
              <AIImage src={agent.avatar || `/placeholder-${agent.role.toLowerCase()}.png`} alt={agent.role} />
            </AIAvatar>
            <AIRole>{agent.role}</AIRole>
          </AIOption>
        ))}
        <AIOption>
          <AddButton>+</AddButton>
        </AIOption>
      </AIGrid>
      
      <StartButton onClick={handleStart}>Start</StartButton>
    </Container>
  );
};

export default SelectAIPartners;
