// frontend/src/components/SelectAIPartners.js
import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  padding: 12px;
  height: 100vh;
`;

const Header = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 24px;
`;

const BackButton = styled.button`
  background: none;
  border: none;
  font-size: 20px;
  cursor: pointer;
`;

const Title = styled.h1`
  font-size: 24px;
  margin-left: 12px;
`;

const AIGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr); /* 2 columns for smaller screen */
  gap: 16px;
  margin-bottom: 24px;
`;

const AIOption = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  cursor: pointer;
  padding: 5px;
  border-radius: 10px;
`;

const AIAvatar = styled.div`
  width: 80px;
  height: 80px;
  background-color: #e0e0e0;
  border-radius: 10px;
  margin-bottom: 8px;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;
  border: ${props => props.selected ? '3px solid rgb(197, 152, 54)' : 'none'};
`;

const AIImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const AIRole = styled.div`
  font-size: 14px;
  font-weight: bold;
  text-align: center;
`;

const AddButton = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 80px;
  height: 80px;
  background-color: #f0f0f0;
  border: 2px dashed #aaa;
  border-radius: 10px;
  cursor: pointer;
  font-size: 24px;
`;

const StartButton = styled.button`
  padding: 12px 24px;
  font-size: 16px;
  background-color: #e0e0e0;
  border: none;
  border-radius: 24px;
  cursor: pointer;
  align-self: center;
  margin-top: auto;

  &:hover {
    background-color: #d0d0d0;
  }
`;

const SelectAIPartners = () => {
  const [aiAgents, setAiAgents] = useState([
    { id: 1, role: 'Designer', avatar: '/img/designer.png' },
    { id: 2, role: 'Engineer', avatar: '/img/engineer.png' },
    { id: 3, role: 'Finance', avatar: '/img/finance.png' },
    { id: 4, role: 'Professor', avatar: '/img/professor.png' }
  ]);
  const [selectedAgents, setSelectedAgents] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  
  const navigate = useNavigate();
  const location = useLocation();
//   const params = new URLSearchParams(location.search);
//   const roomId = params.get('roomId');
const roomId = location.state?.roomId;
  
  useEffect(() => {
    // Fetch AI agents from backend
    const fetchAIAgents = async () => {
      try {
        const response = await axios.get('/api/ai-agents/');
        if (response.data.length > 0) {
          setAiAgents(response.data);
        }
      } catch (error) {
        console.error('Error fetching AI agents:', error);
      }
    };
    
    fetchAIAgents();
  }, []);
  
  const toggleAgent = (agent) => {
    if (selectedAgents.find(a => a.id === agent.id)) {
      setSelectedAgents(selectedAgents.filter(a => a.id !== agent.id));
    } else {
      setSelectedAgents([...selectedAgents, agent]);
    }
  };
  
  const handleStart = async () => {
    // 檢查是否有 roomId
    if (!roomId) {
      console.error('Room ID is missing');
      alert('無法加入房間，缺少房間 ID');
      return;
    }
    
    // 檢查身份 - 使用兩種可能的 key 來獲取用戶名稱
    const username = localStorage.getItem('username') || localStorage.getItem('userName');
    
    if (!username) {
      console.error('Username not found in localStorage');
      alert('請先設定您的用戶名稱');
      navigate('/');
      return;
    }
    
    setIsLoading(true);
    
    try {
      console.log(`Joining room ${roomId} as ${username}`);
      
      // 將選定的 AI 代理轉換為伙伴格式
      const aiPartners = selectedAgents.map(agent => ({
        id: agent.id,
        name: agent.role,
        role: agent.role
      }));
      
      // 直接導航到房間，把 AI 伙伴作為狀態傳遞
      navigate(`/room/${roomId}`, {
        state: {
          aiPartners: aiPartners
        }
      });
    } catch (error) {
      console.error('Error starting room:', error);
      alert('加入房間時發生錯誤，請稍後再試');
      setIsLoading(false);
    }
  };
  
  return (
    <Container>
      <Header>
        <BackButton onClick={() => navigate('/')}>←</BackButton>
        <Title>Select AI partners...</Title>
      </Header>
      
      <AIGrid>
        {aiAgents.map((agent) => (
          <AIOption 
            key={agent.id} 
            selected={selectedAgents.some(a => a.id === agent.id)}
            onClick={() => toggleAgent(agent)}
          >
            <AIAvatar selected={selectedAgents.some(a => a.id === agent.id)}>
              <AIImage src={agent.avatar || `/placeholder-${agent.role.toLowerCase()}.png`} alt={agent.role} />
            </AIAvatar>
            <AIRole>{agent.role}</AIRole>
          </AIOption>
        ))}
        <AIOption>
          <AddButton>+</AddButton>
        </AIOption>
      </AIGrid>
      
      <StartButton 
        onClick={handleStart} 
        disabled={isLoading}
      >
        {isLoading ? 'Loading...' : 'Start'}
      </StartButton>
    </Container>
  );
};

export default SelectAIPartners;
